import { useState } from "react";
import Todo from "./components/Todo";
import Form from "./components/Form";
import FilterButton from "./components/FilterButton";
import { nanoid } from "nanoid";
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd'

const FILTER_OPTIONS = {
  All: () => true,
  Active: todo => !todo.completed,
  Completed: todo => todo.completed
};

const FILTER_NAMES = Object.keys(FILTER_OPTIONS);

function App(props) {
  const [todos, setTodos] = useState(props.todos);
  const [filter, setFilter] = useState("All");
  const [theme, setTheme] = useState("dark");


  function addTodo(name) { 
    const newTodo = { id: nanoid(), name: name, completed: false };
    setTodos([...todos, newTodo]);
  }
  function deleteTodo(id) {
    const remainingTodos = todos.filter(todo => id !== todo.id);
    setTodos(remainingTodos);
  }
  function handleOnDragEnd (result)  {
    if (!result.destination) return;

    const items = [...todos]
    const [reorderedItem] = items.splice(result.source.index, 1)
    items.splice(result.destination.index, 0, reorderedItem)
    setTodos(items)
  }
  const todoList = todos.filter(FILTER_OPTIONS[filter]).map((todo, index) => {
    return (
      <Draggable key = {index} draggableId={todo.id} index={index}>
      {(provided) => (
            <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>

    <Todo id={todo.id} name={todo.name} completed={todo.completed} key={index} deleteTodo={deleteTodo} handleOnDragEnd={handleOnDragEnd} index={index}   toggleTodo={toggleTodo} />
    </li>
              )}
              </Draggable>
    )
    }
  )
  const numberOfItems = todos.filter(todo => !todo.completed).length
  const itemsSpelling = todos.filter(todo => !todo.completed).length !== 1 ? "items" : "item";
  const filterList = FILTER_NAMES.map(name => (
    <FilterButton key={name} name={name} isPressed={name === filter} setFilter={setFilter} />
  ));
  const toggleTheme = () => {
    const newTheme= theme === "dark" ? "light" : "dark";
    setTheme(newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
  }

  const toggleTodo = id => {
    const idx = todos.findIndex(todo => todo.id === id)
    const newTodos = [...todos]

    newTodos[idx].completed = !newTodos[idx].completed
    setTodos(newTodos)
  }
  return (
    <div className='container-fluid'>
      <div className="hero-img"></div>
      <div className="container">
        <div className="header">
          <h1 className="title">Todo</h1>
          <div className="theme-icon" onClick={toggleTheme}></div>
        </div>
        <Form onSubmit={addTodo}/>
        <div className="list-wrapper">
        <DragDropContext onDragEnd={handleOnDragEnd}>
      <Droppable droppableId='todos'>
        {provided => (
          <ul aria-labelledby="list-heading" {...provided.droppableProps} ref={provided.innerRef}>
           {todoList}

            {provided.placeholder}
          </ul>
          )}
          </Droppable>
        </DragDropContext>
          <div className="form-footer">
            <div className="items-left">{numberOfItems} {itemsSpelling} left
            </div>
            <div className="form-filters filter">
              {filterList}
            </div>
            <div className="clear">
              <button onClick={() => setTodos(todos.filter(todo => !todo.completed))}>Clear Completed
              </button>
            </div>
          </div>   
        </div>
        <div className="footer-info">Drag and drop to reorder list
        </div>  
      </div>
    </div>

  );
}

export default App;
